import axios from "axios";
import { useState } from "react";

export const AgregaJuegos = () => {
  const config = {
        credentials: 'include',
        headers: 
        {
        'Content-Type': 'application/json'
        }
    };
  const iniciojuegoState = {
    id: null,
    Nombre: "",
    Categoria: "",
    Lanzamiento: "",
    Reseña: "",
  };
  const [juego, setJuego] = useState(iniciojuegoState);
  const [subida, setSubida] = useState(false);
  const handleMenuChange = (e) => {
    const { name, value } = e.target;
    setJuego({ ...juego, [name]: value });
  };
  const subirJuego = () => {
    let data = {
      Nombre: juego.Nombre,
      Categoria: juego.Categoria,
      Lanzamiento: juego.Lanzamiento,
      Reseña: juego.Reseña,
    };
    axios
        .post(`http://127.0.0.1:8000/api/Juego/`, data, config)
        .then((response) => {
            setJuego({
                id: response.data.id,
                Nombre: response.data.Nombre,
                Categoria: response.data.Categoria,
                Lanzamiento: response.data.Lanzamiento,
                Reseña: response.data.Reseña,

            });
            setSubida(true);
            console.log(response.data);
        })
        .catch((e) => {
            console.error(e);
        });
  };
  const nuevoJuego = () => {
    setJuego(iniciojuegoState);
    setSubida(false);
  };
