import React from "react";
import { useState, useEffect, useRef } from 'react';
import axios from "axios";
import { APIURL, headers } from "./../servicios/servicio";
import { useHistory } from "react-router-dom";

export const ListaJuegos = () => {
  const [juegos, setJuegos] = useState([]);
  const history = useHistory();
  const countRef = useRef(0);
  const [eliminado, setElimina] = useState(false); // deleted -> eliminado
  useEffect(() => { recuperaAllLista();}, [countRef]);

  const recuperaAllLista = () => { 

    const config = {
        credentials: 'include',
        headers: 
        {
        'Content-Type': 'application/json'
        }
    };
    axios 
        .get(`http://127.0.0.1:8000/api/Juego/`, config)
        .then((response) => { setJuegos(response.data);})
        .catch((e) => { console.error(e);});
  };

  const EliminaJuego = (id) => {
    axios
        .delete(`${APIURL}/Juego/${id}/`, { headers: {headers,},})
        .then((response) => { setElimina(true); recuperaAllLista();})
        .catch((e) => { console.error(e);});
  };
  const handleUpdateClick = (id) => { history.push(`/Juego/${id}/update/`);};


return (
    <div className="row justify-content-center">
        <div className="col">
        {eliminado && (
            <div
            className="alert alert-danger alert-dismissible fade show"
            role="alert"
            >
            Rese単a del juego eliminado.
            <button
                type="button"
                className="close"
                data-dismiss="alert"
                aria-label="Close"
            >
                <span aria-hidden="true">&times;</span>
            </button>
            </div>
        )}
        {juegos &&
            juegos.map((juegos) => (
            <div className="card my-3 w-25 mx-auto">
                <div className="card-body">
                <h2 className="card-title font-weight-bold">{juegos.Nombre}</h2>
                <h4 className="card-subtitle mb-2">{juegos.Categoria}</h4>
                <h4 className="card-subtitle mb-2">{juegos.Lanzamiento}</h4>
                <p className="card-text">{juegos.Rese単a}</p>
                </div>
                <div classNameName="card-footer">
                <div
                    className="btn-group justify-content-around w-75 mb-1 "
                    data-toggle="buttons"
                >
                    <span>
                    <button
                        className="btn btn-info"
                        onClick={() => handleUpdateClick(juegos.id)}
                    >
                        Update
                    </button>
                    </span>
                    <span>
                    <button
                        className="btn btn-danger"
                        onClick={() => EliminaJuego(juegos.id)}
                    >
                        Delete
                    </button>
                    </span>
                </div>
                </div>
            </div>
            ))}
        </div>
    </div>

  );
};
